import React from "react";
import aboutUs1 from "../../img/about_us_1.png";

import styled from "styled-components";
import { About, Description, Image, Hide, Ellipse } from "../../styles";
//Framer Motion
import { motion } from "framer-motion";
import { titleAnim, fade, photoAnim } from "../../animation";

const AboutSection = () => {
  return (
    <StyledAbout className="styled_about">
      <Ellipse />
      <AboutDescription>
        <motion.div className="aboutHeader">
          <Hide>
            <motion.h2 className="landing__title" variants={titleAnim}>
              We are here to inject brands with infectious energy in finding
              their purpose and voice.
            </motion.h2>
          </Hide>
        </motion.div>
        <AboutHide>
          <motion.button className="explore" variants={fade}>
            Explore Services
          </motion.button>
          <motion.button variants={fade}>Get in touch</motion.button>
        </AboutHide>
      </AboutDescription>
      <AboutUsImage>
        <motion.img
          variants={photoAnim}
          src={aboutUs1}
          alt="guy with a camera"
        />
      </AboutUsImage>
    </StyledAbout>
  );
};

//Styled Components
const StyledAbout = styled(About)`
  position: relative;
  height: 91.5vh;
  padding: 0rem;
  background: #000;
  overflow: hidden;

  @media (max-width: 768px) {
    position: absolute;
    top: -5rem;
  }
`;

const AboutDescription = styled(Description)`
  position: absolute;
  top: 80%;
  left: 50%;
  transform: translate(-50%, -80%);
  z-index: 3;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  row-gap: 2rem;
  padding: 0 !important;
  width: 60%;

  @media only screen and (max-width: 600px) {
    width: 100%;
    top: 95%;

    h2 {
      width: 100%;
      font-size: 2rem;
    }
  }

  @media only screen and (max-width: 768px) {
    top: 50rem;

    h2 {
      width: 100%;
      font-size: 3rem;
      line-height: 95.7%;
    }
  }

  .aboutHeader {
    h2 {
      text-align: center;
      font-family: "Baloo 2", cursive;
      font-size: 3.1rem;
      font-style: normal;
      font-weight: 700;
      line-height: 100.7%;

      background: linear-gradient(78deg, #f00 0%, #ffcc0a 100%);
      background-clip: text;
      -webkit-background-clip: text;
      -webkit-text-fill-color: transparent;
    }

    @media only screen and (max-width: 768px) {
      top: 50rem;

      h2 {
        text-align: center;
        font-family: "Baloo 2", cursive;
        font-size: 2.5rem;
        font-style: normal;
        font-weight: 700;
        line-height: 103.7%;
        background: linear-gradient(78deg, #f00 0%, #ffcc0a 100%);
        background-clip: border-box;
        background-clip: text;
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;
        letter-spacing: 0.00001rem;
        padding: 0 2rem;
      }
    }
  }
`;
const AboutUsImage = styled(Image)`
  width: 100%;
  height: 100%;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;

    @media (max-width: 768px) {
      transform: scale(1.4) translateX(3rem) !important;
    }
  }
`;

const AboutHide = styled(Hide)`
  display: flex;
  column-gap: 2rem;
  flex-wrap: wrap;

  button {
    border: 2px solid #ffcc0a;
    transition: all 0.75s ease;

    &:nth-child(1) {
      border-radius: 0.70909rem;
      background: #ffcc0a;
      color: #000000;

      &:hover {
        background: #000 !important;
        color: white;
        border: 2px solid #ffcc0a;
      }
    }
    &:nth-child(2) {
      border-radius: 0.70909rem;
      border: 2px solid #ffcc0a;
      color: #ffffff;
    }
  }

  @media (max-width: 768px) {
    flex-direction: column;
    width: 100%;
    padding: 0 1.5rem;

    button {
      flex: 1;
      padding: 1.5rem 0;

      &:nth-child(1) {
        margin: 0rem 0 0rem 0 !important;
      }
    }
  }
`;
export default AboutSection;
